import { Component } from '@angular/core';
import { TodoItem } from "../todoitem";
import { Model } from '../model';

@Component({
  selector: 'app-todo',
  templateUrl: './todo.component.html',
  styleUrls: ['./todo.component.css']
})
export class TodoComponent {

  displayAll: boolean = false;
  inputText: string="";

  constructor() { }

  model = new Model();

  getName() {
    return this.model.name;
  }

  addItem() {
    if (this.inputText != "") { 
      this.model.items.push({ description: this.inputText, action: false }) 

    }
    else {
      alert("bilgi giriniz.")
    }
  }

  getItems() {
    if (this.displayAll) {
      return this.model.items;
    }
    return this.model.items.filter(item => !item.action)
  }

  displayCount(){
    return this.model.items.filter(i=>i.action).length;
  }

}
<div class="container">
  <div class="dov row">
    <div class="col-5 mx-auto">
      <h3 class="bg-primary text-white p-3">
        {{ getName() }} kullanıcısının listesi
      </h3>

      <div class="input-group mb-2">
        <input
          #input
          type="text"
          [(ngModel)]="inputText"
          class="form-control"
          (keyup.enter)="addItem()"
        />

        <button class="btn btn-outline-primary" (click)="addItem()">
          Add
        </button>
      </div>
      <div class="form-check mb3">
        <input
          type="checkbox"
          [(ngModel)]="displayAll"
          class="form-check-input"
          id="displayAll"
        />
        <label for="displayAll" class="form-check-label">Hepsini Göster</label>
      </div>

      <div
        class="alert alert-warning"
        *ngIf="getItems().length == 0; else itemsTable"
      >
        Tüm görevler tamamlandı.
      </div>

      <div class="my-2">{{ displayCount() }} görev tamamlandı.</div>

      <ng-template #itemsTable>
        <table class="table table-bordered">
          <thead>
            <tr>
              <th></th>
              <th>Description</th>
              <th>Action</th>
              <th>Status</th>
            </tr>
          </thead>
          <tr *ngFor="let item of getItems(); let i = index" [class]="{'bg-primary':item.action,'bg-warning':!item.action}">
            <td>{{ i + 1 }}</td>
            <td>{{ item.description }}</td>
            <td>
              <!-- {{ item.action }} -->
              <!-- <div [ngSwitch]="item.action">
              <div *ngSwitchCase="true">Yes</div>
              <div *ngSwitchDefault>No</div>
            </div> -->

              <div *ngIf="item.action; else noTemplate">Yes</div>

              <ng-template #noTemplate> No </ng-template>
            </td>
            <td>
              <input type="checkbox" [(ngModel)]="item.action" name="" id="" />
            </td>
          </tr>
        </table>
      </ng-template>
    </div>
  </div>
</div>
