import { Component } from '@angular/core';
import { TodoItem } from "../todoitem";
import { Model } from '../model';

@Component({
  selector: 'app-todo',
  templateUrl: './todo.component.html',
  styleUrls: ['./todo.component.css']
})
export class TodoComponent {

  displayAll: boolean=true;

constructor(){}

model = new Model();

getName(){
  return this.model.name;
}

addItem(value:string){
  if(value!="")
  {this.model.items.push({description:value,action:"no"})}
  else{
    alert("bilgi giriniz.")
  }
}

getItems(){
  return this.model.items
}

}
<div class="container">
  <div class="dov row">
    <div class="col-5 mx-auto">
      <h3 class="bg-primary text-white p-3">{{getName()}} kullanıcısının listesi</h3>

      <div class="input-group mb-2">
        <input #input type="text" class="form-control" 
        (blur)="addItem(input.value)"
        (keyup.enter)="addItem(input.value)">

        <button class="btn btn-outline-primary" (click)="addItem(input.value)">Add</button>
      </div>
      <div class="form-check mb3">
        <input type="checkbox" [(ngModel)]="displayAll" class="form-check-input" id="displayAll">
        <label for="displayAll" class="form-check-label">Hepsini Göster</label>
      </div>

      <table class="table table-bordered">
        <thead>
          <tr> 
            <th></th>
            <th>Description</th>
            <th>Action</th>
          </tr>
        </thead>
        <tr *ngFor="let item of getItems(); let i = index;">
          <td>{{i+1}}</td>
          <td>{{item.description}}</td>
          <td>{{item.action}}</td>
        </tr>

      </table>

    </div>
  </div>
</div>
